INSERT INTO `sxunt_diary` VALUES ('41', '17', 'uploadimg/1421039479.JPG', '晴', '2015-01-10', '上海绚瑞', 'SHEN', 'SHEN时装2015春夏新品走秀现场', '<p style=\"text-align: center;\"><span style=\"color: rgb(155, 187, 89);\">单反拍摄</span></p><p><embed allowscriptaccess=\"sameDomain\" quality=\"high\" src=\"http://player.youku.com/player.php/sid/XODY5OTY3MjI0/v.swf\" type=\"application/x-shockwave-flash\" height=\"508\" width=\"100%\"/></p><p><br/></p><p style=\"text-align: center;\"><span style=\"color: rgb(155, 187, 89);\">摄影机全程固定拍</span><br/></p><p><embed allowscriptaccess=\"sameDomain\" quality=\"high\" src=\"http://player.youku.com/player.php/sid/XODcwMjQ3MzY4/v.swf\" type=\"application/x-shockwave-flash\" height=\"508\" width=\"100%\"/></p>', '小文', 'SHEN,2015春夏，走秀', 'YOHOOO!!在这灯光闪烁T台下，醉了、醉了！看得我都醉了！~旁边一哥们按快门按到手软！谁叫你丫带那么重的准备。SHEN新的T台感觉还不错、就是现场灯光感觉有些不足！、、');
