INSERT INTO `sxunt_edify` VALUES ('10', 'Gyeonlee', '', 'http://img.sxunt.com/uploads/image//20141230/1419916743.jpg', '2', '1417605258', 'true', '1', 'Gyeonlee', '佳能EOS 5D Mark III', '广工', '<br />陌生人，当你看著我。<br />眼睛带有神秘、 是一个谜。<br />带著微笑看来， 你的温柔，如此迷离。 <br />陌生人你还会记得？<br /> 陌生人让我 ，记住你。带回， 是逝去， 我梦到,那片林中灯火。', '', '105', '陌生人，当你看著我。<br />\r\n<br />\r\n眼睛带有神秘、 是一个谜。<br />\r\n<br />\r\n带著微笑看来， 你的温柔，如此迷离。 <br />\r\n<br />\r\n陌生人你还会记得？<br />\r\n<br />\r\n陌生人让我 ，记住你。带回， 是逝去， 我梦到,那片林中灯火。', 'http://img.sxunt.com/uploads/image//20141219/1419000798.jpg');
INSERT INTO `sxunt_edify` VALUES ('11', 'THE WITCH\'S DIARY', 't', 'http://img.sxunt.com/uploads/image//20141230/1419911888.jpg', '3', '1417606196', 'true', '1', 'ING 影路人', '佳能Eos 5D Mark II ', '四川自贡', '<br/>游走在无日的云霭,<br />昏暗的烛光点亮黎明的前奏。<br />放走了爱\'人，<br />束缚了自己，Farewell……<br />写进日记最后的一节……<br />……', '', '111', '游走在无日的云霭，<br />\r\n<br />\r\n昏暗的烛光点亮黎明的前奏。<br />\r\n<br />\r\n放走了爱人，束缚了自己，<br />\r\n<br />\r\nFarewell……<br />\r\n<br />\r\n写进日记最后的一节……\r\n……', 'http://img.sxunt.com/uploads/image//20141219/1419001592.jpg');
