INSERT INTO `sxunt_edify` VALUES ('21', '大岗老屋', 't', 'http://img.sxunt.com/uploads/image//20141230/1419907756.jpg', '11', '1417692798', 'true', '5', '花月佳期摄影', '', '大岗老屋', '近来很少很少拍一些老屋子，硬盘里找一个文件有关一些童年的暗影回忆............里面有很多传闻！！！<br />', '', '95', '近来很少很少拍一些老屋子，硬盘里找一个文件有关一些童年的暗影回忆............里面有很多传闻！！！<br />\r\n但今天它已经离开了我们视线了只有这几张照片和回忆。进入老屋也不是传闻那样的黑暗，总给我的感觉只有安静............', 'http://img.sxunt.com/uploads/image//20141219/1419000452.jpg');
